window.onload=function(){var windowHeight=window.innerHeight,pageHeight=document.body.offsetHeight,totalHeight=pageHeight-windowHeight,barWidth=0,windowWidth=document.body.clientWidth;var leftColor="#f22eff",rightColor="#f22e00";var progressBar=document.createElement("div");progressBar.style.cssText+=";position:fixed;top:0;height:3px;background:linear-gradient(to right, "+leftColor+", "+rightColor+");z-index:10002;";document.body.appendChild(progressBar);window.onscroll=function(e){reCalcBarW()};window.onresize=function(e){windowHeight=window.innerHeight;totalHeight=pageHeight-windowHeight;windowWidth=window.innerWidth;reCalcBarW()};function reCalcBarW(){barWidth=windowWidth*window.scrollY/totalHeight;progressBar.style.width=barWidth+"px"}reCalcBarW();setInterval(function(){if(pageHeight!=document.body.clientHeight){pageHeight=document.body.clientHeight;totalHeight=pageHeight-windowHeight;reCalcBarW()}},500)};